INSERT INTO `Book` 
  (book_id, title, author, genre, publish_year, stock, available_number, borrowed_number, image_url, description)
VALUES
(1,  'To Kill a Mockingbird',                   'Harper Lee',                   'Fiction',           1960, 10, 10, 0, 'http://localhost:5173/asset/images/books/To Kill a Mockingbird.jpg',                            'Một câu chuyện hấp dẫn, đau lòng và hoàn toàn đáng chú ý về tuổi mới lớn ở miền Nam bị đầu độc bởi định kiến ​​​​độc hại, câu chuyện nhìn nhận một thế giới tươi đẹp và tội ác tàn bạo qua con mắt của một cô gái trẻ, khi cha cô — một luật sư địa phương đấu tranh — mạo hiểm mọi thứ để bảo vệ một người đàn ông da đen bị buộc tội oan về một tội ác khủng khiếp.'),
(2,  'Nineteen Eighty-Four',                    'George Orwell',                'Dystopian',         1949, 8,  8,  0, 'http://localhost:5173/asset/images/books/Nineteen Eighty-Four.jpg',                             'Tiểu thuyết phản địa đàng mô tả xã hội bị kiểm soát toàn diện và sự đấu tranh vì tự do.'),
(3,  'The Great Gatsby',                        'F. Scott Fitzgerald',          'Classic',           1925, 7,  7,  0, 'http://localhost:5173/asset/images/books/The Great Gatsby.jpg',                                 'Câu chuyện về Jay Gatsby giàu có một cách bí ẩn và tình yêu của anh dành cho người đẹp Daisy Buchanan, về những bữa tiệc xa hoa trên Đảo Long Island, đây là câu chuyện được xây dựng tinh tế về nước Mỹ vào những năm 1920.'),
(4,  'Pride and Prejudice',                     'Jane Austen',                  'Romance',           1813, 12, 12, 0, 'http://localhost:5173/asset/images/books/Pride and Prejudice.jpg',                              'Cuốn tiểu thuyết được chuyển thể của Jane Austen nổi tiếng với nhân vật nữ chính dí dỏm, nhiệt huyết, những câu chuyện tình lãng mạn giật gân và những nhận xét khéo léo về những chiến thắng và cạm bẫy của quy ước xã hội.'),
(5,  'The Catcher in the Rye',                  'J.D. Salinger',                'Fiction',           1951, 9,  9,  0, 'http://localhost:5173/asset/images/books/The Catcher in the Rye.jpg',                           'The Catcher in the Rye là tác phẩm kinh điển mọi thời đại trong văn học về tuổi mới lớn - một bài điếu văn về sự xa lánh của tuổi mới lớn, nắm bắt được nhu cầu kết nối sâu sắc của con người và cảm giác mất mát khó hiểu khi chúng ta rời xa tuổi thơ.'),
(6,  'The Hobbit',                              'J.R.R. Tolkien',               'Fantasy',           1937, 11, 11, 0, 'http://localhost:5173/asset/images/books/The Hobbit.jpg',                                       'The Hobbit là câu chuyện về Bilbo Baggins… một người Hobbit trầm tính và mãn nguyện, cuộc sống của anh bị đảo lộn khi anh tham gia cùng phù thủy Gandalf và mười ba chú lùn trong hành trình đòi lại kho báu bị đánh cắp của những chú lùn. Đây là một hành trình đầy rẫy nguy hiểm – và cuối cùng, Bilbo là người duy nhất phải đối mặt với người bảo vệ kho báu này, con rồng đáng sợ nhất Smaug.'),
(7,  'Moby-Dick',                               'Herman Melville',              'Adventure',         1851, 5,  5,  0, 'http://localhost:5173/asset/images/books/Moby-Dick.jpg',                                        'Trong một chuyến đi trước, một con cá voi trắng bí ẩn đã xé toạc chân của một thuyền trưởng tên là Ahab. Bây giờ, phi hành đoàn của Pequod, trong một cuộc truy đuổi liên tục phiêu lưu và những tai nạn khủng khiếp, phải theo chân Ahab điên rồ vào vực thẳm để thỏa mãn cơn khát trả thù không thể dập tắt của anh ta.'),
(8,  'War and Peace',                           'Leo Tolstoy',                  'Historical',        1869, 6,  6,  0, 'http://localhost:5173/asset/images/books/War and Peace.jpg',                                    'Chiến tranh và hòa bình tập trung chủ yếu vào cuộc xâm lược Nga của Napoleon vào năm 1812 và theo chân ba nhân vật nổi tiếng nhất trong văn học: Pierre Bezukhov, con trai ngoài giá thú của một bá tước đang đấu tranh giành quyền thừa kế và khao khát sự hoàn thiện về mặt tinh thần; Hoàng tử Andrei Bolkonsky, người rời bỏ gia đình để chiến đấu trong cuộc chiến chống lại Napoleon; và Natasha Rostov, cô con gái trẻ xinh đẹp của một nhà quý tộc, người đã thu hút cả hai người đàn ông.'),
(9,  'Crime and Punishment',                    'Fyodor Dostoevsky',            'Classic',           1866, 8,  8,  0, 'http://localhost:5173/asset/images/books/Crime and Punishment.jpg',                             'Raskolnikov, một cựu sinh viên nghèo khổ và tuyệt vọng, lang thang qua khu ổ chuột của St Petersburg và phạm tội giết người ngẫu nhiên mà không hề hối hận hay hối hận. Anh ta tưởng tượng mình là một người đàn ông vĩ đại, một Napoleon: hành động vì một mục đích cao cả hơn ngoài luật đạo đức thông thường. Nhưng khi anh ta bắt đầu một trò chơi mèo vờn chuột nguy hiểm với một cảnh sát điều tra đáng ngờ, Raskolnikov bị truy đuổi bởi tiếng nói ngày càng lớn của lương tâm và thấy sợi dây thòng lọng tội lỗi của chính mình đang xiết chặt quanh cổ anh ta. Chỉ có Sonya, một gái mại dâm bị áp bức, mới có thể mang đến cơ hội cứu rỗi.'),
(10, 'Truyện Kiều',                             'Nguyễn Du',                    'Poetry',            1813, 4,  4,  0, 'http://localhost:5173/asset/images/books/Truyện Kiều.jpg',                                      'Truyện Kiều của Nguyễn Du là tác phẩm tiêu biểu của văn học Việt Nam, kể về cuộc đời truân chuyên của Thúy Kiều – một người con gái tài sắc vẹn toàn nhưng phải trải qua nhiều sóng gió.'),
(11, 'Số đỏ',                                   'Vũ Trọng Phụng',               'Literature',        1936, 7,  7,  0, 'http://localhost:5173/asset/images/books/Số đỏ.jpg',                                            'Tác phẩm xoay quanh nhân vật chính là Xuân - biệt danh là Xuân Tóc đỏ - từ chỗ là một kẻ bị coi là hạ lưu, bỗng chốc đã bước lên tầng lớp danh giá của xã hội nhờ trào lưu Âu hóa của giới tiểu tư sản Hà Nội khi đó.'),
(12, 'Dế mèn phiêu lưu ký',                     'Tô Hoài',                      'Children\'s Literature', 1941, 10, 10, 0, 'http://localhost:5173/asset/images/books/Dế mèn phiêu lưu ký.jpg',                         'Tác phẩm miêu tả cuộc phiêu lưu của một chú Dế Mèn qua thế giới loài vật và loài người. Những vấn đề nóng hổi như là: cái thiện và cái ác, chiến tranh và hòa bình, lí tưởng và lẽ sống được thể hiện một cách nhẹ nhàng, tinh tế mà sâu sắc.'),
(13, 'Nhật ký Đặng Thùy Trâm',                  'Đặng Thùy Trâm',               'Memoir',            2005, 5,  5,  0, 'http://localhost:5173/asset/images/books/Nhật ký Đặng Thùy Trâm.jpg',                           'Một cuốn nhật kí nhặt được bên xác của một nữ Việt Cộng đã suýt bị người lính Mỹ ném vào lửa, nhưng người phiên dịch đã khuyên anh ta nên giữ lại vì "trong đó có lửa". Nhật kí Đặng Thùy Trâm là những ghi chép hàng ngày của một người nữ bác sĩ về cuộc sống của chị nơi chiến tuyến. Cuốn nhật kí là thế giới riêng của người trí thức nhạy cảm mà không yếu đuối, tha thiết với cuộc sống mà không hề sợ hãi trước những gian nan. Ở đó ta vẫn gặp những băn khoăn trăn trở trước tình yêu, trước cuộc sống phức tạp hàng ngày, những nỗi buồn, nỗi nhớ nhung, sự cô đơn của một người con gái, nhưng đồng thời chúng ta cũng thấy được một ý chí mãnh liệt, những lời nói tự động viên cảnh tỉnh, một lòng can đảm phi thường - những điều đã làm nên một thế hệ anh hùng.'),
(14, 'Đắc nhân tâm',                            'Dale Carnegie (dịch)',         'Self-Help',         1936, 9,  9,  0, 'http://localhost:5173/asset/images/books/Đắc nhân tâm.jpg',                                     'Đắc nhân tâm của Dale Carnegie là quyển sách duy nhất về thể loại self-help liên tục đứng đầu danh mục sách bán chạy nhất (best-selling Books) do báo The New York Times bình chọn suốt 10 năm liền. Được xuất bản năm 1936, với số lượng bán ra hơn 15 triệu bản, tính đến nay, sách đã được dịch ra ở hầu hết các ngôn ngữ, trong đó có cả Việt Nam, và đã nhận được sự đón tiếp nhiệt tình của đọc giả ở hầu hết các quốc gia.'),
(15, 'Harry Potter and the Sorcerer''s Stone',  'J.K. Rowling',                 'Fantasy',           1997, 14, 14, 0, 'http://localhost:5173/asset/images/books/Harry Potter and the Sorcerer''s Stone.jpg',           'Harry Potter thậm chí còn chưa từng nghe đến Hogwarts khi những lá thư bắt đầu rơi xuống thảm chùi chân ở số bốn, Privet Drive. Được ghi địa chỉ bằng mực xanh trên giấy da màu vàng với con dấu màu tím, chúng nhanh chóng bị tịch thu bởi dì và chú ghê rợn của cậu. Sau đó, vào sinh nhật thứ mười một của Harry, một người đàn ông khổng lồ mắt bọ hung tên là Rubeus Hagrid đột nhiên xuất hiện với một tin tức đáng kinh ngạc: Harry Potter là một phù thủy và cậu có một vị trí tại Trường Phù thủy và Pháp sư Hogwarts. Một cuộc phiêu lưu đáng kinh ngạc sắp bắt đầu!'),
(16, 'The Alchemist',                           'Paulo Coelho',                 'Fiction/Fantasy',   1988, 12, 12, 0, 'http://localhost:5173/asset/images/books/The Alchemist.jpg',                                    'Câu chuyện huyền bí về Santiago, một cậu bé chăn cừu Andalusia khao khát được đi du lịch để tìm kiếm kho báu thế gian. Cuộc hành trình của cậu sẽ dẫn cậu đến với sự giàu có khác xa—và thỏa mãn hơn nhiều—so với những gì cậu từng tưởng tượng. Hành trình của Santiago dạy chúng ta về sự khôn ngoan thiết yếu của việc lắng nghe trái tim mình, nhận ra cơ hội và học cách đọc những điềm báo rải rác trên con đường cuộc sống, và quan trọng nhất là theo đuổi ước mơ của mình.'),
(17, 'Sapiens: A Brief History of Humankind',   'Yuval Noah Harari',            'History',           2011, 8,  8,  0, 'http://localhost:5173/asset/images/books/Sapiens A Brief History of Humankind.jpg',             'Từ một nhà sử học nổi tiếng, một câu chuyện mang tính đột phá về sự sáng tạo và tiến hóa của loài người - một cuốn sách bán chạy nhất quốc tế - khám phá những cách mà sinh học và lịch sử đã định nghĩa chúng ta và nâng cao hiểu biết của chúng ta về ý nghĩa của việc trở thành "con người".'),
(18, 'Chí Phèo',                                'Nam Cao',                      'Short Story',       1941, 6,  6,  0, 'http://localhost:5173/asset/images/books/Chí Phèo.jpg',                                         'Chí Phèo là một truyện ngắn nổi tiếng của nhà văn Nam Cao viết vào tháng 2 năm 1941. Chí Phèo là một tác phẩm xuất sắc, thể hiện nghệ thuật viết truyện độc đáo của Nam Cao, đồng thời là một tấn bi kịch của một người nông dân nghèo bị tha hóa trong xã hội.'),
(19, 'One Hundred Years of Solitude',           'Gabriel García Márquez',       'Magical Realism',   1967, 7,  7,  0, 'http://localhost:5173/asset/images/books/One Hundred Years of Solitude.jpg',                    'Cuốn tiểu thuyết xuất sắc, bán chạy nhất và mang tính bước ngoặt này kể về câu chuyện của gia đình Buendia và ghi lại cuộc xung đột không thể hòa giải giữa mong muốn cô đơn và nhu cầu tình yêu - bằng văn xuôi phong phú, giàu trí tưởng tượng đã định hình nên toàn bộ thể loại được gọi là "chủ nghĩa hiện thực huyền diệu".'),
(20, 'Mắt Biếc',                                'Nguyễn Nhật Ánh',              'Literature',        1990, 5,  5,  0, 'http://localhost:5173/asset/images/books/Mắt Biếc.jpg',                                         'Một tác phẩm được nhiều người bình chọn là hay nhất của nhà văn này. Một tác phẩm đang được dịch và giới thiệu tại Nhật Bản (theo thông tin từ các báo)… Bởi sự trong sáng của một tình cảm, bởi cái kết thúc rất, rất buồn khi suốt câu chuyện vẫn là những điều vui, buồn lẫn lộn (cái kết thúc không như mong đợi của mọi người). Cũng bởi, mắt biếc… năm xưa nay đâu (theo lời một bài hát).'),
(21, 'The Road',                                'Cormac McCarthy',              'Post-Apocalyptic',  2006, 6,  6,  0, 'http://localhost:5173/asset/images/books/The Road.jpg',                                         'Một người cha và con trai đơn độc đi qua nước Mỹ bị thiêu rụi. Không có gì chuyển động trong cảnh quan bị tàn phá ngoại trừ tro bụi trong gió. Trời lạnh đến mức làm nứt đá, và khi tuyết rơi thì xám xịt. Bầu trời tối đen. Điểm đến của họ là bờ biển, mặc dù họ không biết điều gì, nếu có, đang chờ họ ở đó. Họ chẳng có gì; chỉ có một khẩu súng lục để tự vệ trước những băng đảng vô luật pháp rình rập trên đường, quần áo họ đang mặc, một xe chở thức ăn đã nhặt được—và cả nhau.'),
(22, 'Bố già',                                  'Mario Puzo (dịch)',            'Novel',             1969, 7,  7,  0, 'http://localhost:5173/asset/images/books/Bố già.jpg',                                           'Thế giới ngầm được phản ánh trong tiểu thuyết Bố già là sự gặp gỡ giữa một bên là ý chí cương cường và nền tảng gia tộc chặt chẽ theo truyền thống Mafia xứ Sicily với một bên là xã hội Mỹ nhập nhằng đen trắng, mảnh đất màu mỡ cho những cơ hội làm ăn bất chính hứa hẹn những món lợi kếch xù. Trong thế giới ấy, hình tượng Bố già được tác giả dày công khắc họa đã trở thành bức chân dung bất hủ trong lòng người đọc.'),
(23, 'Cho tôi xin một vé đi tuổi thơ',          'Nguyễn Nhật Ánh',              'Children\'s Literature', 2008, 9, 9, 0,'http://localhost:5173/asset/images/books/Cho tôi xin một vé đi tuổi thơ.jpg',                 'Truyện Cho tôi xin một vé đi tuổi thơ là sáng tác mới nhất của nhà văn Nguyễn Nhật Ánh. Nhà văn mời người đọc lên chuyến tàu quay ngược trở lại thăm tuổi thơ và tình bạn dễ thương của 4 bạn nhỏ. Những trò chơi dễ thương thời bé, tính cách thật thà, thẳng thắn một cách thông minh và dại dột, những ước mơ tự do trong lòng… khiến cuốn sách có thể làm các bậc phụ huynh lo lắng rồi thở phào. Không chỉ thích hợp với người đọc trẻ, cuốn sách còn có thể hấp dẫn và thực sự có ích cho người lớn trong quan hệ với con mình.'),
(24, 'Dune',                                    'Frank Herbert',                'Science Fiction',   1965, 9,  9,  0, 'http://localhost:5173/asset/images/books/Dune.jpg',                                             'Lấy bối cảnh trên hành tinh sa mạc Arrakis, Dune là câu chuyện về cậu bé Paul Atreides, người thừa kế của một gia đình quý tộc được giao nhiệm vụ cai trị một thế giới khắc nghiệt, nơi thứ duy nhất có giá trị là melange "gia vị", một loại thuốc có khả năng kéo dài sự sống và nâng cao nhận thức. Được thèm muốn trên khắp vũ trụ đã biết, melange là một giải thưởng đáng để giết... Khi Nhà Atreides bị phản bội, sự hủy diệt của gia đình Paul sẽ đưa cậu bé vào một hành trình hướng tới một số phận lớn hơn mà cậu có thể tưởng tượng. Và khi cậu tiến hóa thành người đàn ông bí ẩn được gọi là Muad\'Dib, cậu sẽ biến giấc mơ cổ xưa và không thể đạt được nhất của loài người thành hiện thực.'),
(25, 'Jane Eyre',                               'Charlotte Brontë',             'Classic',           1847, 7,  7,  0, 'http://localhost:5173/asset/images/books/Jane Eyre.jpg',                                        'Charlotte Brontë kể câu chuyện về cô nhi Jane Eyre, người lớn lên trong ngôi nhà của người dì vô tâm, chịu đựng sự cô đơn và tàn nhẫn. Tuổi thơ đầy rắc rối này đã củng cố tính cách độc lập và tinh thần của Jane - điều này tỏ ra là cần thiết khi cô tìm được việc làm gia sư cho đứa trẻ được Byronic bảo trợ, ông Rochester u sầu. Khi tình cảm của cô dành cho Rochester phát triển, Jane dần khám phá ra bí mật khủng khiếp của Thornfield Hall, buộc cô phải đưa ra lựa chọn. Cô có nên ở lại với Rochester và sống với hậu quả, hay theo đuổi niềm tin của mình - ngay cả khi điều đó có nghĩa là phải rời xa người đàn ông cô yêu?'),
(26, 'The Picture of Dorian Gray',              'Oscar Wilde',                  'Gothic',            1890, 6,  6,  0, 'http://localhost:5173/asset/images/books/The Picture of Dorian Gray.jpg',                       'Mê mẩn trước bức chân dung tuyệt đẹp của chính mình, Dorian Gray đã đánh đổi tâm hồn mình để lấy tuổi trẻ và vẻ đẹp vĩnh cửu. Bị ảnh hưởng bởi người bạn Lord Henry Wotton, anh bị cuốn vào cuộc sống hai mặt đồi trụy, thỏa mãn ham muốn của mình một cách bí mật trong khi vẫn là một quý ông trong mắt xã hội lịch sự. Chỉ có bức chân dung của anh mới mang dấu vết của sự suy đồi. The Picture of Dorian Gray là một succès de scandale. Những độc giả đầu tiên đã bị sốc bởi những ám chỉ về những tội lỗi không thể nói ra của nó, và cuốn sách sau đó đã được sử dụng làm bằng chứng chống lại Wilde tại Old Bailey vào năm 1895.'),
(27, 'Frankenstein',                            'Mary Shelley',                 'Science Fiction',   1818, 8,  8,  0, 'http://localhost:5173/asset/images/books/Frankenstein.jpg',                                     'Tiểu thuyết có ảnh hưởng của Mary Shelley về nhà khoa học có sáng tạo trở thành quái vật.'),
(28, 'The Lord of the Rings',                   'J.R.R. Tolkien',               'Fantasy',           1954, 12, 12, 0, 'http://localhost:5173/asset/images/books/The Lord of the Rings.jpg',                            'Chúa tể những chiếc nhẫn kể về nhiệm vụ vĩ đại được thực hiện bởi Frodo và Hiệp hội bảo vệ nhẫn: Phù thủy Gandalf; những người Hobbit Merry, Pippin và Sam; Người lùn Gimli; Người tiên Legolas; Boromir xứ Gondor; và một người lạ cao lớn, bí ẩn tên là Strider.'),
(29, 'The Little Prince',                       'Antoine de Saint-Exupéry',     'Fable',             1943, 10, 10, 0, 'http://localhost:5173/asset/images/books/The Little Prince.jpg',                                'Một phi công bị mắc kẹt trong sa mạc thức dậy vào một buổi sáng và thấy một cậu bé phi thường nhất đang đứng trước mặt mình. "Làm ơn", người lạ mặt hỏi, "vẽ cho tôi một con cừu". Và phi công nhận ra rằng khi những sự kiện trong cuộc sống quá khó hiểu, không còn lựa chọn nào khác ngoài việc khuất phục trước những điều bí ẩn của chúng. Anh ta lấy bút chì và giấy ra... Và thế là câu chuyện ngụ ngôn khôn ngoan và hấp dẫn này bắt đầu, khi dạy cho chúng ta bí mật về điều thực sự quan trọng trong cuộc sống, đã thay đổi thế giới mãi mãi đối với độc giả của nó.'),
(30, 'Don Quixote',                             'Miguel de Cervantes',          'Classic',           1605, 5,  5,  0, 'http://localhost:5173/asset/images/books/Don Quixote.jpg',                                      'Don Quixote đã trở nên say mê đọc những tiểu thuyết hiệp sĩ đến nỗi anh quyết định trở thành một hiệp sĩ du mục. Cùng với người hầu trung thành của mình, Sancho Panza, những chiến công của anh nở rộ theo đủ mọi cách tuyệt vời. Trong khi trí tưởng tượng của Quixote thường khiến anh lạc lối—anh nghiêng mình trước những chiếc cối xay gió, tưởng tượng chúng là những người khổng lồ—Sancho đã có được sự xảo quyệt và một sự sáng suốt nhất định. Một kẻ điên tỉnh táo và một kẻ ngốc khôn ngoan, họ cùng nhau lang thang khắp thế giới, và cùng nhau ám ảnh trí tưởng tượng của độc giả trong gần bốn trăm năm.'),
(31, 'The Old Man and the Sea',                 'Ernest Hemingway',             'Literature',        1952, 9,  9,  0, 'http://localhost:5173/asset/images/books/The Old Man and the Sea.jpg',                          'Tiểu thuyết ngắn này, vốn đã là một tác phẩm kinh điển hiện đại, là câu chuyện bi thảm được kể lại tuyệt vời về một ngư dân Cuba ở Dòng hải lưu vùng Vịnh và con cá Marlin khổng lồ mà anh ta giết và làm mất—được nhắc đến cụ thể trong trích dẫn kèm theo Giải thưởng Nobel văn học của tác giả năm 1954.'),
(32, 'The Brothers Karamazov',                  'Fyodor Dostoevsky',            'Philosophical',     1880, 6,  6,  0, 'http://localhost:5173/asset/images/books/The Brothers Karamazov.jpg',                           'The Brothers Karamazov là một vụ án mạng bí ẩn, một bộ phim truyền hình về tòa án và là cuộc khám phá về sự ganh đua khiêu dâm trong một loạt các mối tình tay ba liên quan đến Fyodor Pavlovich Karamazov "độc ác và đa cảm" cùng ba người con trai của ông ta - Dmitri bốc đồng và gợi cảm; Ivan lạnh lùng nhưng lý trí; và Alyosha, một cô gái trẻ khỏe mạnh, má đỏ. Thông qua các sự kiện hấp dẫn trong câu chuyện của họ, Dostoevsky đã miêu tả toàn bộ cuộc sống của người Nga, là sự phấn đấu về mặt xã hội và tinh thần, trong thời kỳ vừa là thời kỳ hoàng kim vừa là bước ngoặt bi thảm trong nền văn hóa Nga.'),
(33, 'Anna Karenina',                           'Leo Tolstoy',                  'Romance',           1878, 7,  7,  0, 'http://localhost:5173/asset/images/books/Anna Karenina.jpg',                                    'Anna là một người phụ nữ tinh tế từ bỏ cuộc sống trống rỗng của mình với tư cách là vợ của Karenin và tìm đến Bá tước Vronsky để thỏa mãn bản chất đam mê của mình - với hậu quả bi thảm. Levin là sự phản ánh của chính Tolstoy, thường thể hiện quan điểm và niềm tin của riêng tác giả.'),
(34, 'Les Misérables',                          'Victor Hugo',                  'Historical',        1862, 8,  8,  0, 'http://localhost:5173/asset/images/books/Les Misérables.jpg',                                   'Câu chuyện về sự bất công, chủ nghĩa anh hùng và tình yêu của Victor Hugo kể về số phận của Jean Valjean, một tên tù trốn thoát quyết tâm quên đi quá khứ tội phạm của mình. Nhưng những nỗ lực của anh để trở thành một thành viên được kính trọng của cộng đồng liên tục bị đe dọa: bởi chính lương tâm của anh, khi, do một trường hợp nhầm lẫn danh tính, một người đàn ông khác bị bắt thay anh; và bởi những cuộc điều tra không ngừng nghỉ của Thanh tra Javert kiên trì. Tuy nhiên, Valjean không chỉ phải tự do vì bản thân mình, vì anh đã thề sẽ bảo vệ đứa con gái sơ sinh của Fantine, người đã bị đẩy vào con đường mại dâm vì nghèo đói.'),
(35, '1984',                                    'George Orwell',                'Dystopian',         1949, 10, 10, 0, 'http://localhost:5173/asset/images/books/1984.jpg',                                             'Tiểu thuyết phản địa đàng mô tả xã hội bị kiểm soát toàn diện và sự đấu tranh vì tự do.'),
(36, 'Totto-chan: The Little Girl at the Window', 'Tetsuko Kuroyanagi',         'Memoir',            1981, 6,  6,  0, 'http://localhost:5173/asset/images/books/Totto-chan.jpg',                                       'Bộ truyện hấp dẫn về những hồi ức thời thơ ấu này kể về một ngôi trường lý tưởng ở Tokyo trong Thế chiến II, nơi kết hợp việc học với niềm vui, sự tự do và tình yêu. Ngôi trường khác thường này có những toa tàu hỏa cũ làm lớp học và được điều hành bởi một người đàn ông phi thường - người sáng lập và hiệu trưởng, Sosaku Kobayashi - người luôn tin tưởng vào quyền tự do ngôn luận và hoạt động.'),
(37, 'Những người khốn khổ',                    'Victor Hugo (dịch)',           'Historical',        1862, 5,  5,  0, 'http://localhost:5173/asset/images/books/Những người khốn khổ.jpg',                             'Những người khốn khổ là câu chuyện về xã hội nước Pháp trong khoảng hơn 20 năm đầu thế kỷ 19 kể từ thời điểm Napoléon I lên ngôi và vài thập niên sau đó. Nhân vật chính của tiểu thuyết là Jean Valjean, một cựu tù khổ sai tìm cách chuộc lại những lỗi lầm gây ra thời trai trẻ. Bộ tiểu thuyết không chỉ nói tới bản chất của cái tốt, cái xấu, của luật pháp, mà tác phẩm còn là cuốn bách khoa thư đồ sộ về lịch sử, kiến trúc của Paris, nền chính trị, triết lý, luật pháp, công lý, tín ngưỡng của nước Pháp nửa đầu thế kỷ 19.'),
(38, 'Tuổi thơ dữ dội',                         'Phùng Quán',                   'War',               1988, 7,  7,  0, 'http://localhost:5173/asset/images/books/Tuổi thơ dữ dội.jpg',                                  '"Tuổi thơ dữ dội" là một tác phẩm truyện dài bảy phần của nhà văn Phùng Quán. Truyện được khởi thảo bên bờ Hồ Tây năm 1968 và hoàn thành trong lều cỏ giữa hồ Tịnh Tâm năm 1986. Cuốn truyện xoay quanh cuộc sống chiến đấu và sự hy sinh của những thiếu niên 13, 14 tuổi trong hàng ngũ Đội thiếu niên trinh sát của trung đoàn Trần Cao Vân.'),
(39, 'Lão Hạc',                                 'Nam Cao',                      'Short Story',       1943, 4,  4,  0, 'http://localhost:5173/asset/images/books/Lão Hạc.jpg',                                          'Lão Hạc, một người nông dân chất phác, hiền lành. Lão góa vợ và có một người con trai nhưng vì quá nghèo nên không thể lấy vợ cho người con trai của mình. Người con trai lão vì thế đã rời bỏ quê hương để đến đồn điền cao su làm ăn kiếm tiền. Lão luôn trăn trở, suy nghĩ về tương lai của đứa con. Lão sống bằng nghề làm vườn, mảnh vườn mà vợ lão đã mất bao công sức để mua về và để lại cho con trai lão. So với những người khác lúc đó, gia cảnh của lão khá đầy đủ, tuy nhiên do ốm yếu hơn hai tháng và cũng vì trận bão mà lão không có việc gì để làm. Lão đã rất dằn vặt bản thân mình khi mang một "tội lỗi" là đã nỡ tâm "lừa một con chó". Lão đã khóc rất nhiều với ông giáo (người hàng xóm thân thiết của lão).'),
(40, 'Tắt đèn',                                 'Ngô Tất Tố',                   'Realism',           1939, 6,  6,  0, 'http://localhost:5173/asset/images/books/Tắt đèn.jpg',                                          'Tác phẩm xoanh quanh nhân vật chính là chị Dậu và gia đình – một điển hình của cuộc sống bần cùng hóa do sưu cao thuế nặng mà chế độ thực dân áp đặt lên xã hội Việt Nam. Tác phẩm này đã vạch trần bộ mặt tàn ác, bất nhân của xã hội thực dân nửa phong kiến đương thời. Đỉnh điểm của cơn cùng cực là việc chị Dậu phải bán con, khoai và bán cả bầy chó để lấy tiền nộp sưu thuế cho chồng và cảnh chị Dậu chạy ra giữa màn trời đêm tối đen như mực và như cái tiền đồ của chị.'),
(41, 'Chữ người tử tù',                         'Nguyễn Tuân',                  'Short Story',       1960, 4,  4,  0, 'http://localhost:5173/asset/images/books/Chữ người tử tù.jpg',                                  'Huấn Cao vốn là một nhà nho có tài viết chữ nhanh và đẹp, nhưng vì chống lại triều đình mà trở thành tử tù. Trước ngày bị xử tử, Huấn Cao bị giải đến nhà ngục nơi có viên cai ngục và thầy thơ lại, những người rất hâm mộ tài viết chữ của Huấn Cao. Trong những ngày bị giam ở đây, Huấn Cao được viên quản ngục và thầy thơ lại đối đãi rất tốt. Khi viên quản ngục nhận được tin ngày xử tử Huấn Cao đã tới gần, ông liền cùng thầy thơ lại vào nhà ngục để hoàn thành tâm nguyện là xin Huấn Cao cho chữ. Huấn Cao vì cảm mến thái độ "biệt nhỡn liên tài" và tấm lòng yêu cái đẹp của viên quản ngục nên đã đồng ý cho chữ.'),
(42, 'Chiếc thuyền ngoài xa',                   'Nguyễn Minh Châu',             'Short Story',       1983, 5,  5,  0, 'http://localhost:5173/asset/images/books/Chiếc thuyền ngoài xa.jpg',                            'Câu chuyện kể về chuyến đi của một nghệ sĩ nhiếp ảnh đến một vùng biển miền Trung để chụp ảnh nghệ thuật, với một cốt truyện nhiều tình huống bất ngờ với hệ thống nhân vật đa dạng, nhà văn đề cập đến tính trung thực của người nghệ sĩ, nêu lên mối quan hệ chặt chẽ giữa văn học và hiện thực cũng như những vấn đề phức tạp của cuộc sống, kể cả bi kịch số phận con người.'),
(43, 'Vợ nhặt',                                 'Kim Lân',                      'Short Story',       1962, 6,  6,  0, 'http://localhost:5173/asset/images/books/Vợ nhặt.jpg',                                          'Truyện ngắn Vợ nhặt viết về nạn đói năm Ất Dậu (1945). Tiền thân của truyện ngắn này là tiểu thuyết Xóm ngụ cư mà Kim Lân viết ngay sau Cách mạng tháng Tám. Tuy nhiên, bản thảo tiểu thuyết còn dang dở và bị thất lạc. Sau đó, tác giả đã dựa vào cốt truyện của tiểu thuyết này để viết về truyện ngắn Vợ nhặt.'),
(44, 'Đời thừa',                                'Nam Cao',                      'Psychological',     1944, 5,  5,  0, 'http://localhost:5173/asset/images/books/Đời thừa.jpg',                                         'Đời thừa là tập truyện ngắn gồm 13 truyện ngắn Đời thừa, Chuyện tình, Đòn chồng, Giăng sáng, Ở hiền, Trẻ con không biết đói, Một đám cưới, Bài học quét nhà, Đui mù, Đôi móng giò, Đón khách, Từ ngày mẹ chết, Thôi, đi về.'),
(45, 'Gone with the Wind',                      'Margaret Mitchell',            'Historical Romance',1936, 8,  8,  0, 'http://localhost:5173/asset/images/books/Gone with the Wind.jpg',                               'Đây là câu chuyện về Scarlett O\'Hara, cô con gái hư hỏng, hay thao túng của một chủ đồn điền giàu có, người đã đến tuổi thiếu nữ đúng lúc để chứng kiến ​​Nội chiến thay đổi mãi mãi cách sống của mình. Một câu chuyện sâu sắc về niềm đam mê và lòng dũng cảm, trong các trang của Cuốn theo chiều gió, Margaret Mitchell đã tái hiện những nhân vật khó quên đã thu hút độc giả trong hơn bảy mươi năm.'),
(46, 'A Brief History of Time',                 'Stephen Hawking',              'Science',           1988, 7,  7,  0, 'http://localhost:5173/asset/images/books/A Brief History of Time.jpg',                          'Được kể bằng ngôn ngữ mà tất cả chúng ta đều có thể hiểu, Lược sử thời gian đắm chìm vào cõi kỳ lạ của lỗ đen và quark, của phản vật chất và "mũi tên thời gian", của vụ nổ lớn và một vị Chúa lớn hơn—nơi những khả năng thật kỳ diệu và bất ngờ. Với những hình ảnh thú vị và trí tưởng tượng sâu sắc, Stephen Hawking đưa chúng ta đến gần hơn với những bí mật tối thượng ở chính trung tâm của sự sáng tạo.'),
(47, 'Thinking, Fast and Slow',                 'Daniel Kahneman',              'Psychology',        2011, 6,  6,  0, 'http://localhost:5173/asset/images/books/Thinking, Fast and Slow.jpg',                          'Thu hút người đọc vào một cuộc trò chuyện sôi nổi về cách chúng ta suy nghĩ, Kahneman tiết lộ những nơi chúng ta có thể và không thể tin tưởng vào trực giác của mình và cách chúng ta có thể khai thác những lợi ích của tư duy chậm. Ông đưa ra những hiểu biết thực tế và sáng suốt về cách đưa ra lựa chọn trong cả công việc kinh doanh và cuộc sống cá nhân của chúng ta—và cách chúng ta có thể sử dụng các kỹ thuật khác nhau để bảo vệ bản thân khỏi những trục trặc về tinh thần thường khiến chúng ta gặp rắc rối. Tư duy nhanh và chậm sẽ thay đổi cách bạn nghĩ về tư duy.'),
(48, 'Nhà giả kim',                             'Paulo Coelho (dịch)',          'Fiction',           1988, 9,  9,  0, 'http://localhost:5173/asset/images/books/Nhà giả kim.jpg',                                      'Tiểu thuyết Nhà giả kim của Paulo Coelho như một câu chuyện cổ tích giản dị, nhân ái, giàu chất thơ, thấm đẫm những minh triết huyền bí của phương Đông. Trong lần xuất bản đầu tiên tại Brazil vào năm 1988, sách chỉ bán được 900 bản. Nhưng, với số phận đặc biệt của cuốn sách dành cho toàn nhân loại, vượt ra ngoài biên giới quốc gia, Nhà giả kim đã làm rung động hàng triệu tâm hồn, trở thành một trong những cuốn sách bán chạy nhất mọi thời đại, và có thể làm thay đổi cuộc đời người đọc.'),
(49, 'Tôi thấy hoa vàng trên cỏ xanh',          'Nguyễn Nhật Ánh',              'Literature',        2010, 7,  7,  0, 'http://localhost:5173/asset/images/books/Tôi thấy hoa vàng trên cỏ xanh.jpg',                   'Câu chuyện của Tôi Thấy Hoa Vàng Trên Cỏ Xanh có chút này chút kia, để ai soi vào cũng thấy mình trong đó, kiểu như lá thư tình đầu đời của cu Thiều chẳng hạ ngây ngô và khờ khạo. Nhưng Tôi Thấy Hoa Vàng Trên Cỏ Xanh hình như không còn trong trẻo, thuần khiết trọn vẹn của một thế giới tuổi thơ nữa. Cuốn sách nhỏ nhắn vẫn hồn hậu, dí dỏm, ngọt ngào nhưng lại phảng phất nỗi buồn, về một người cha bệnh tật trốn nhà vì không muốn làm khổ vợ con, về một người cha khác giả làm vua bởi đứa con tâm thầm của ông luôn nghĩ mình là công chúa, Những bài học về luân lý, về tình người trở đi trở lại trong day dứt và tiếc nuối. Tôi Thấy Hoa Vàng Trên Cỏ Xanh lắng đọng nhẹ nhàng trong tâm tưởng để rồi ai đã lỡ đọc rồi mà muốn quên đi thì thật khó.'),
(50, 'Rừng Na Uy',                              'Haruki Murakami',              'Romance',           1987, 8,  8,  0, 'http://localhost:5173/asset/images/books/Rừng Na Uy.jpg',                                       'Câu chuyện xảy ra với bối cảnh là nước Nhật những năm 1960, khi mà thanh niên Nhật Bản, như thanh niên nhiều nước khác đương thời, đấu tranh chống lại những định kiến tồn tại trong xã hội. Murakami miêu tả những sinh viên cải cách này như những tên đạo đức giả và thiếu sự kiên định.');
